angular.module("upmApp").controller("dashboardCtrl",["$scope","$cookies","api","User",function(){}]),angular.module("upmApp").controller("loginController",["$scope","$cookies","api","User","$location","snackbar",function($scope,$cookies,api,User,$location,snackbar){$scope.text="Hola mundo",$scope.sendForm=function(user){api.login(user.email,user.password).success(function(data){snackbar.message(data.message),$scope.getUser()}).error(function(data){snackbar.error(data.error)})},$scope.signIn=function(){$location.path("/signin")},$scope.getUser=function(){api.getUser().success(function(data){User.update(data),$location.path("/dashboard")}).error(function(){console.log("error")})}}]);